  const headers = {

    'Content-Type': 'application/json',

    if (token) {
    headers['Authorization'] = `Bearer ${token}`
    }
}
return headers
}

const callApi = async(endpointPath, {method, body, token}) => {
    const {options} = {
      method: method || 'GET',
      headers: makeHeaders()

    }
    const response = await fetch(`${BASEURL}${endpointPath}`)
    const result = await response.json()

}


