import React, { useEffect, useState, } from "react"
import ReactDOM from "react-dom/client"
import {FetchPost} from "./api/Api"
import {BrowserRouter, Route, Link, Switch, useHistory } from "react-router-dom";
import { Posts, Register, Login, CreatePost, Profile, UserPost } from "./components";












const App = () => {
    const [posts, setPosts] = useState([])
    const [token, setToken] = useState(window.localStorage.getItem("token" || ""))
    const [userPost, setUserPost] = useState([])




console.log(userPost)




    useEffect(() => {
        async function LoadPosts() {
        setPosts(await FetchPost())
        }
        LoadPosts();
      }, []); // Or [] if effect doesn't need props or state


      const history = useHistory();




      useEffect(() => {
        if (localStorage.getItem("token")){
            setToken(localStorage.getItem("token"))
        }else {
        history.push("/login")
        }
            }, []);


    return (
        <>

        <header className="siteheader">
        <img className="logo"src="https://wallpaperaccess.com/full/1920259.jpg"/>
        <nav className="links">


            <Link to="/" className="link-one">Home</Link>

            <Link className="link-one" to="/profile">Profile</Link>


            <Link className="link-two" to="/posts">Posts</Link>
           {
            token ? <a href="#" onClick={() => 
                {
                    setToken('')
                    setUserPost([])
                    localStorage.removeItem("token")

                    console.log("user post on logout", userPost)
                    history.push("/login")
                }}className="link">Logout</a>: 
            <>
            <Link className="link-three" to="/login">Login</Link> 
            <Link className="link-five" to="/register">Register</Link>
            </>
           }






        </nav>

        </header>


            <Switch>
            <Route exact path = "/">

            </Route>
            <Route  exact path="/posts">
            <Posts posts={posts}/>
            </Route>







            <Route  path="/register">
            <Register setToken={setToken}/>

            </Route>

            <Route path="/login">
            <Login setUserPost={setUserPost} setToken={setToken}/>
            </Route>



            </Switch>

            <Route path="/makepost">
            <CreatePost token={token}/>
            </Route>



            <Route path="/profile">
                <Profile />
            </Route>


            <Route path="/mypost">
                <UserPost userPost={userPost}/>
            </Route>



        </> 

    )
}









const root = ReactDOM.createRoot(document.getElementById('app'))
root.render(
    <BrowserRouter>
    <App/>
    </BrowserRouter>
)
