import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom/client";
import { FetchPost } from "./api/Api";
import {
  BrowserRouter,
  Route,
  Link,
  Switch,
  useHistory,
} from "react-router-dom";
import { Posts, Register, Login, CreatePost, Profile } from "./components";

const App = () => {
  const [posts, setPosts] = useState([]);
  const [token, setToken] = useState(
    window.localStorage.getItem("token" || "")
  );

  console.log("All post", posts);

  useEffect(() => {
    async function LoadPosts() {
      setPosts(await FetchPost());
    }
    LoadPosts();
  }, []); // Or [] if effect doesn't need props or state

  const history = useHistory();

  useEffect(() => {
    if (localStorage.getItem("token")) {
      setToken(localStorage.getItem("token"));
    } else {
      history.push("/login");
    }
  }, [token]);

  return (
    <>
      <header className="siteheader">
        <img
          className="logo"
          src="https://wallpaperaccess.com/full/1920259.jpg"
        />
        <nav className="links">
          <Link to="/" className="link-one">
            Home
          </Link>

          <Link className="link-two" to="/profile">
            Profile
          </Link>

          <Link className="link-three" to="/posts">
            Posts
          </Link>

          {token ? (
            <a
              href="#"
              onClick={() => {
                setToken("");

                localStorage.removeItem("token");

                console.log("user post on logout", userPost);
                history.push("/login");
              }}
              className="link-six"
            >
              Logout
            </a>
          ) : (
            <>
              <Link className="link-four" to="/login">
                Login
              </Link>
              <Link className="link-five" to="/register">
                Register
              </Link>
            </>
          )}
        </nav>
      </header>

      <Switch>
        <Route exact path="/"></Route>

        <Route exact path="/posts">
          <Posts posts={posts} />
        </Route>

        <Route path="/register">
          <Register setToken={setToken} />
        </Route>

        <Route path="/login">
          <Login setToken={setToken} />
        </Route>
      </Switch>

      <Route path="/makepost">
        <CreatePost setPosts={setPosts} token={token} />
      </Route>

      <Route path="/profile">
        <Profile />
      </Route>

      <Route path="/mypost"></Route>
    </>
  );
};

const root = ReactDOM.createRoot(document.getElementById("app"));
root.render(
  <BrowserRouter>
    <App />
  </BrowserRouter>
);
