import {Link} from "react-router-dom"
import {default as Postitem} from "./Postsitem"


const Posts = ({posts, token, setPosts }) => {
const [searchInput, setSearchInput] = useState('')




const handlsubmit = (event) => {
event.preventDefault()
const filteredPost = posts.filter (post =>
searchInput === post.description || searchInput === location || searchInput === AuthenticatorAssertionResponse.username

)
console.log(filteredPost)

}


return (




<div className="posts-body">
<div className="post-search">

    <form onSubmit={handlsubmit}>
    <input value={searchInput} onChange={(event) => setSearchInput(event.target.value)}className="search-post"  placeholder="Search for a Post"></input>
    <button type="submit" className="create-post">Search Post</button>
    </form>

    {token ?
    <>

    <Link to ="/makepost"className="create-post">Create a post</Link>
    </> 
    : 
    <div className="create-post">Create an account!</div>}
    </div>

        { posts.map((post) =>
         <Postitem 
         key={post._id}
         post={post} 
         token={token} 
         setPosts={setPosts}/>

        )}
</div>
)}










export default Posts
