import React, {useState} from "react";
import {Link} from "react-router-dom"
import {default as Postitem} from "./Postsitem"


const Posts = ({posts, token, setPosts, PostsResults, setPostsResult }) => {
const [searchInput, setSearchInput] = useState('')




const handlesubmit = (event) => {
    console.log("Seach Input Here:", searchInput)
    event.preventDefault()
    console.log("submitted!")
    const filteredPost = posts.filter ((post) =>
        post.description.toLowerCase().includes(searchInput) || post.author.username.toLowerCase().includes(searchInput) || post.price.toLowerCase().includes(searchInput) || post.title.includes(searchInput) || post.location.includes(searchInput)
)
setPostsResult(filteredPost)
}




return (
<div className="posts-body">
<div className="post-search">
    <form onSubmit={handlesubmit}>
    <input value={searchInputtoLowerCase()} onChange={(event) => setSearchInput(event.target.value)}className="search-post"  placeholder="Search for a Post"></input>
    <button type="submit" className="search-post-button">Search Post</button>
    </form>
    <button onClick={() => setPostsResult(posts)} className="back-button">Back</button>
    {token ?
    <>
    <Link to ="/makepost"className="create-post">Create a post</Link>
    </> 
    : 
    <div className="create-post">Create an account!</div>}
    </div>

        { PostsResults.map((post) =>
         <Postitem 
         key={post._id}
         post={post} 
         token={token} 
         setPosts={setPosts}/>

        )}
</div>
)}










export default Posts
